import React, { useState, useEffect } from 'react';
import ResourceCalcInput from '../Components/ResourceCalcInput';
import ResourceCalcOutput from '../Components/ResourceCalcOutput';

const ResourceCalc = () => {
    const [resourceCalcResult, setResourceCalcResult] = useState(null);
    const [demand, setDemand] = useState(null);
    const [difference, setDifference] = useState(null);


    const calculateResource = (variables_object) => {
        let result = 0
        let A = 0
        let B = 0
        let C = 0
        let demand = 0
        A = variables_object.variable_A
        B = variables_object.variable_B
        C = variables_object.variable_C
        demand = parseInt(variables_object.demand)
        result = (A*B*C)
        let difference = demand - (A*B*C)
        setResourceCalcResult(result)
        setDemand(demand)
        setDifference(difference)
    }

    return(
        <>
        <ResourceCalcInput 
        calculateResource={calculateResource}/>
        <ResourceCalcOutput 
        resourceCalcResult={resourceCalcResult}
        demand={demand}
        difference={difference}
        />
        </>
    )

}

export default ResourceCalc;
