import React, { useState, useEffect } from 'react';


const ResourceCalcInput = ({calculateResource}) => {
     const [thickness, setThickness] = useState(null);
     const [variableB, setVariableB] = useState(null);
     const [variableC, setVariableC] = useState(null);
     const [demand, setDemand] = useState(null);

    const handleThicknessChange = (ev) => {
        setVariableA(ev.target.value)
    }

    const handleVariableBChange = (ev) => {
        setVariableB(ev.target.value)
    }

    const handleVariableCChange = (ev) => {
        setVariableC(ev.target.value)
    }

    const handleDemandChange = (ev) => {
        setDemand(ev.target.value)
    }

    const handleSubmit = (ev) => {
        calculateResource({
            thickness: thickness, 
            variable_B: variableB,
            variable_C: variableC,
            demand: demand
        })
    }

    return(
        <>
        <form>
            <label htmlFor="thickness">Thickness:</label>
            <input  type="text" id="thiskness" onChange={handleThicknessChange}/>
            <label htmlFor="variable_B">Variable B:</label>
            <input  type="text" id="variable_B" onChange={handleVariableBChange}/>
            <label htmlFor="variable_C">Variable C:</label>
            <input  type="text" id="variable_C" onChange={handleVariableCChange}/>
            <label htmlFor="demand">Demand:</label>
            <input  type="text" id="demand" onChange={handleDemandChange}/>
            <input type="button" name="submit" value="Calculate" onClick={handleSubmit}/>
        </form>
        </>
    )

}

export default ResourceCalcInput;
