import Graph from './Graph';
import LocationItem from './LocationItem';
import './LocationList.css'
import { Button,  ButtonGroup, DropdownButton, MenuItem } from 'react-bootstrap';


const LocationList = ({locationData, createGraphWithLocation}) => {
    const locationItems = locationData.map(location => {
        return <LocationItem
        key={location.location_id}
        locationObject={location}
        ></LocationItem>
    });

const handleLocationSelect = (ev) => {
    console.log(ev.target.value)
    createGraphWithLocation({
        location_id: ev.target.value, 
    })

}

    return(

        <div class="dropdown">
        <Button className="btn btn-outline-danger dropdown-toggle" type="button" id="dropdownMenuButton" id="dropdownMenuButton" data-toggle="dropdown" aria-expanded="false">
        <span>Select location</span>
        <i class="fa fa-caret-down"></i>
        </Button>
        <select class="dropdown-menu" aria-labelledby="dropdownMenuButton">
        {locationItems}
        </select>
        </div>

    )
}

export default LocationList;
